mapper.select:
select
count(item.id) as count, min(item.id) as minId
item.id as id, item.name as name, item.condition as condition, parent.id as parent_id, parent.name as parent_name, child.id as child_id, child.name as child_name, grand_child.id as grand_child_id, grand_child.name as grand_child_name, item.brand as brand, item.price as price, item.shipping as shipping, item.description description
from category as parent inner join category as child on parent.id = child.parent_id inner join category as grand_child on child.id = grand_child.parent_id right outer join items as item on item.category_id = grand_child.id
item.id = #{id}
item.id >= #{id}
AND item.id <= #{id}
AND item.name ILIKE '${searchCategory.name}%'
AND parent.id = #{searchCategory.parentId}
AND child.id = #{searchCategory.childId}
AND grand_child.id = #{searchCategory.grandChildId}
AND item.brand ILIKE '${searchCategory.brand}%'
AND item.brand = '${searchCategory.brand}'
order by item.id
desc
limit 30
offset #{id}

mapper.update:
update items set name=#{item.name}, condition=#{item.condition},
category_id=#{item.grandChildId},
brand=#{item.brand}, price=#{item.price}, description=#{item.description} where id=#{item.id}
